import { CryptoPSBT } from '../src';

describe('CryptoPSBT', () => {
  it('test encode', () => {
    const hex =
      '';
    const cryptoPSBT = CryptoPSBT.fromCBOR(Buffer.from(hex, 'hex'));
    const psbtHex =
      '';
    expect(cryptoPSBT.getPSBT().toString('hex')).toBe(psbtHex);
    expect(cryptoPSBT.toCBOR().toString('hex')).toBe(hex.toLowerCase());
    const ur = cryptoPSBT.toUREncoder(1000).nextPart();
    expect(ur).toBe(
      'ur:crypto-',
    );
  });

  it("test construct", () => {
    const psbtHex =
      '';
    const data = Buffer.from(psbtHex, "hex");
    const cryptoPSBT = new CryptoPSBT(data);
    expect(cryptoPSBT.getPSBT().toString('hex')).toBe(psbtHex);
    const ur = cryptoPSBT.toUREncoder(1000).nextPart();
    expect(ur).toBe(
      'ur:crypto-',
    );
  })
});
